import React from 'react'
import './projects.css'
import data from './allData'

function Projects() {
  const projects = data.map((project)=>{
    return(
       <div className="item-from-data">
          <img src={project.img} alt="nisha"/>
          <h2>{project.title}</h2>
          <p>{project.desc}</p>
          <div className="buttons">
            <button className='btn' data-id={project.demo}><a href="https://kelvinhawks.github.io/Emarket.github.io/">view project</a></button>
            <button>View Code</button>
          </div>
        </div>
    )
  })
  const demoBtn = document.querySelectorAll('.btn')
  demoBtn.forEach(demo=>{
    demo.addEventListener('click', (e)=>{
      const target = e.currentTarget.dataset.id
      const href = target.getAttribute('href')
      //console.log(target);
      console.log(href);
    })
  })
 
  return (
    <div className='container'>
      <h1>Projects</h1>
      <div className="content">
           {projects} 
      </div>
    </div>
  )
}

export default Projects
